import { useGlobal } from 'reactn'

const NameForm = () => {
    const [songName, setSongName] = useGlobal("songName")

    const handleChange = (e) => {
        if (e.target.value !== "")
        {
            setSongName(e.target.value)
        }

    }

    return (
        <>
            <input type="text" onChange={handleChange} />
        </>
    )
}

export default NameForm
