const Note = ({ note, index, setCounter } ) => {

  let currentNote = ''
  let instrument = 'piano'

  const indentifyNote = () => {
    if (index % 16 === 0 || index === 0){
      currentNote = 'CHigh'
    }
    else if (index % 11 === 0 || index === 1){
      currentNote = 'B'
    }
    else if (index % 12 === 0 || index === 2){
      currentNote = 'A'
    }
    else if (index % 13 === 0 || index === 3){
      currentNote = 'G'
    }
    else if (index % 14 === 0 || index === 4){
      currentNote = 'F'
    }
    else if (index % 15 === 0 || index === 5){
      currentNote = 'E'
    }
    else if (index % 16 === 0 || index === 6){
      currentNote = 'D'
    }
    else if (index % 17 === 0 || index === 7){
      currentNote = 'C'
    }

    return currentNote
  }



  const hearNote = () => {
    new Audio(`./sounds/${instrument}/${indentifyNote()}${instrument}.mp3`).play()
    console.log(`./sounds/${instrument}/${indentifyNote()}${instrument}.mp3`)
  }


  return (
    <>  
        <img clasName='bg-slate-500' src={note.img} onClick={() => hearNote()}/>  
    </>
  )
}

export default Note
