import React, { useState, useEffect } from 'react'

import Navbar from "./components/Navbar";
import MusicMakerContainer from "./components/MusicMakerContainer";


function App() {
  //Fixes audio delay in Safari 
  const AudioContext = window.AudioContext || window.webkitAudioContext;
  const audioCtx = new AudioContext();

  const numberOfColumns = 80
  const [notes, setNotes ] = useState([])  

  // const rehydrateState = () => {
  //   const state = localStorage.getItem("globalState")
  //   if (state) return JSON.parse(state)

  //   return {
  //     x: [null,null,null,null]
  //   }
  // }
  // setGlobal(rehydrateState())

  // addCallback(state => {
  //   localStorage.setItem("globalState", JSON.stringify(state))
  // })

  useEffect(() => { 
    setNullInArrays()
  })

  useEffect(() => {
    for (let i=0; i < numberOfColumns; i++) {
      setNotes(notes => [...notes, ...notesArray])
    }
  }, [])


  let playList0 = []
  let playList1 = []
  let playList2 = []
  let playList3 = []
  let playList4 = []
  let playList5 = []
  let playList6 = []
  let playList7 = []

  let arraysLists =[
    playList0, playList1, playList2, playList3, playList4, playList5, playList6, playList7
  ]

  const setNullInArrays = () => {
    for (let i=0; i<numberOfColumns; i++){
      playList0.push(null)
      playList1.push(null)
      playList2.push(null)
      playList3.push(null)
      playList4.push(null)
      playList5.push(null)
      playList6.push(null)
      playList7.push(null)
    }
  }

  let instrument = 'piano'
  let loopCounter = 0

  const playSong = () => {
    scrollToTarget(loopCounter)

    if (playList0[loopCounter] === undefined){}
    else {new Audio(`./sounds/${instrument}/${playList0[loopCounter]}${instrument}.mp3`).play()}

    if (playList1[loopCounter] === undefined){}
    else {new Audio(`./sounds/${instrument}/${playList1[loopCounter]}${instrument}.mp3`).play()}

    if (playList2[loopCounter] === undefined){}
    else {new Audio(`./sounds/${instrument}/${playList2[loopCounter]}${instrument}.mp3`).play()}

    if (playList3[loopCounter] === undefined){}
    else {new Audio(`./sounds/${instrument}/${playList3[loopCounter]}${instrument}.mp3`).play()}

    if (playList4[loopCounter] === undefined){}
    else {new Audio(`./sounds/${instrument}/${playList4[loopCounter]}${instrument}.mp3`).play()}

    if (playList5[loopCounter] === undefined){}
    else {new Audio(`./sounds/${instrument}/${playList5[loopCounter]}${instrument}.mp3`).play()}

    if (playList6[loopCounter] === undefined){}
    else {new Audio(`./sounds/${instrument}/${playList6[loopCounter]}${instrument}.mp3`).play()}

    if (playList7[loopCounter] === undefined){}
    else {new Audio(`./sounds/${instrument}/${playList7[loopCounter]}${instrument}.mp3`).play()}

    loopCounter += 1
    // Function Calls itself  
    setTimeout(playSong, 1000);
  }

  let noteSection = document.getElementById('noteSection')
  // noteSection.style.display = 'none'
  console.log(noteSection)


  let duration = 250;
  let distance = -48
  let playInProgress = false
  let noteLength = 1000


  const scrollToTarget = (durationCounter) => {
    console.log('is it running>?')
    distance += 48

    playInProgress = true

    let startTime = null;
    let requestId;
    const loop = function (currentTime) {
        if (!startTime) {
            startTime = currentTime;
        }
        // Elapsed time in miliseconds
        let time = currentTime - startTime;

        let percent = Math.min(time / durationCounter);
        // console.log(noteLength[durationCounter], 'counter', durationCounter)

        // noteSection.scrollTo(startPos + diff * percent, 0);
        noteSection.scrollTo(distance + 48 * percent, 0);
        if (time < durationCounter && playInProgress === true) {
            // Continue moving
            requestId = window.requestAnimationFrame(loop);
            console.log('if')
        } 
        else if (!playInProgress)  {
            window.cancelAnimationFrame(requestId);
            noteSection.scrollTo(0, 0)
            console.log('else if')
        }
    };
    // alert(`${durationCounter}`)
    requestId = window.requestAnimationFrame(loop);
};

  let notesArray = [
    {
      name: 'CHigh',
      img: './images/note.gif'
    },
    {
      name: 'B',
      img: './images/note.gif'
    },
    {
      name: 'A',
      img: './images/note.gif'
    },
    {
      name: 'G',
      img: './images/note.gif'
    },
    {
      name: 'F',
      img: './images/note.gif'
    },
    {
      name: 'E',
      img: './images/note.gif'
    },
    {
      name: 'D',
      img: './images/note.gif'
    },
    {
      name: 'C',
      img: './images/note.gif'
    },
  ]
    return (
      <div className="App">
        <Navbar />
        {notes && <MusicMakerContainer notes={notes} arraysLists={arraysLists} playSong={playSong}/> }
        <p>test</p>
      </div>
    );
}

export default App;
