import Note from "./Note"

const notes = ({notes, counter, setCounter}) => {
  
  const hearNote = () => {
    new Audio(`./sounds/Cpiano.mp3`).play()
  }

    return (
      <>
        {notes.map((note, index) => (
          index % 16 === 0 ? (
            <div className={`column${Math.floor(index / 16)} bg-slate-500 w-4`} >
              <Note note={note} counter={index} setCounter={setCounter} />
            </div>
            )
              
          : 
          
            <div className={`column${Math.floor(index / 16)} bg-slate-500 w-4`} >
              <Note note={note} counter={index} setCounter={setCounter} hearNote={hearNote}/>
            </div>
        
          ))
          }

      </>
    )
}

export default notes
