import { useState, useGlobal } from 'reactn'

const NoteLength = ({item, column, noteLength}) => {
    const [currentInstrument, setCurrentInstrument ] = useGlobal("currentInstrument")
    const [counter, setCounter] = useState(0)
    const [tempo, setTempo] = useGlobal("tempo")

    let halfNoteTempo =  120000 
    let quarterNoteTempo = 60000 
    let eighthNoteTempo = 30000 
    let sixteenthNoteTempo = 15000 

    const changeNoteLength = () => {
        if (counter === 0){
            noteLength[column].length = eighthNoteTempo / tempo
            item.name = '1/8'
            setCounter(counter +1)
        }
        else if (counter === 1){
            noteLength[column].length = sixteenthNoteTempo / tempo
            item.name = '1/16'
            setCounter( counter +1)
        }
        else if (counter === 2){
            noteLength[column].length = halfNoteTempo / tempo
            item.name = '1/2'
            setCounter(counter +1)
        }
        else if (counter === 3){
            noteLength[column].length = quarterNoteTempo / tempo
            item.name = '1/4'
            setCounter(0)
        }
        forceStateRerender()
    }

    const forceStateRerender = () => {
        let stateTrick = currentInstrument
        setCurrentInstrument('fake')
        setCurrentInstrument(stateTrick)
      }

    return (
        <p className="pr-[1rem]" onClick={() => changeNoteLength()}>{item.name }</p>
    )
}

export default NoteLength
