import { useState, useGlobal } from 'reactn'


const Note = ({ note, index, column, arraysLists, instrumentLists } ) => {
  const [currentInstrument, setCurrnetInstrument ] = useGlobal("currentInstrument")

  const hearNote = () => {
    new Audio(`./sounds/${currnetInstrument}/${note.name}${currnetInstrument}.mp3`).play()
    addNoteToArray()
  }

  const addNoteToArray = () => {
    
    if (arraysLists[6][column] !== null) {
      arraysLists[7][column] = note.name
      instrumentLists[7][column] = currentInstrument
    }
    else if (arraysLists[5][column] !== null) {
      arraysLists[6][column] = note.name
      instrumentLists[6][column] = currentInstrument
    }
    else if (arraysLists[4][column] !== null) {
      arraysLists[5][column] = note.name
      instrumentLists[5][column] = currentInstrument
    }
    else if (arraysLists[3][column] !== null) {
      arraysLists[4][column] = note.name
      instrumentLists[4][column] = currentInstrument
    }
    else if (arraysLists[2][column] !== null) {
      arraysLists[3][column] = note.name
      instrumentLists[3][column] = currentInstrument
    }
    else if (arraysLists[1][column] !== null) {
      arraysLists[2][column] = note.name
      instrumentLists[2][column] = currentInstrument
    }
    else if (arraysLists[0][column] !== null) {
      arraysLists[1][column] = note.name
      instrumentLists[1][column] = currentInstrument
    }
    else {
      arraysLists[0][column] = note.name
      instrumentLists[0][column] = currentInstrument
      // note.instrument = currnetInstrument
    }

    // console.log('playList1', arraysLists[0])
    // console.log("PlayListArray", arraysLists)
    
    console.log("Instra list", instrumentLists)
    console.log("current Inst", currentInstrument)
  }
  
 


  return (
    <>  
        <img id={index} className={`note column${column} bg-slate-500`} src={note.img} onClick={() => hearNote()}/>  
    </>
  )
}

export default Note
