import { useState, useGlobal } from 'reactn'


const Note = ({ note, index, column, arraysLists } ) => {


  const hearNote = () => {
    new Audio(`./sounds/${note.instrument}/${note.name}${note.instrument}.mp3`).play()
    addNoteToArray()
  }

  const addNoteToArray = () => {
    if (arraysLists[6][column] !== null) arraysLists[7][column] = note.name
    else if (arraysLists[5][column] !== null) arraysLists[6][column] = note.name
    else if (arraysLists[4][column] !== null) arraysLists[5][column] = note.name
    else if (arraysLists[3][column] !== null) arraysLists[4][column] = note.name
    else if (arraysLists[2][column] !== null) arraysLists[3][column] = note.name
    else if (arraysLists[1][column] !== null) arraysLists[2][column] = note.name
    else if (arraysLists[0][column] !== null) arraysLists[1][column] = note.name
    else arraysLists[0][column] = note.name

    console.log('playList1', arraysLists[0])
    console.log("PlayListArray", arraysLists)
  }
  
 


  return (
    <>  
        <img id={index} className={`note column${column} bg-slate-500`} src={note.img} onClick={() => hearNote()}/>  
    </>
  )
}

export default Note
