import Note from "./Note"
import { useState, useGlobal } from 'reactn'
const notes = ({ arraysLists, instrumentLists, currentInstrument}) => {
  const [notes, setNotes] = useGlobal("notes")
    return (
      <>
        {notes.map((note, index) => (
            <div key={index} className={`column${Math.floor(index / 8)} pr-[1.25rem] `} >
              <Note index={index} notes={notes} note={note} column={Math.floor(index / 8)} arraysLists={arraysLists} instrumentLists={instrumentLists} currentInstrument={currentInstrument}/>
            </div>
          ))
        }
      </>
    )
}

export default notes
