const Note = ({ note, index, setCounter } ) => {

  let currentNote = ''
  let instrument = 'piano'

  const indentifyNote = () => {
    if (index % 8 === 1 || index === 0){
      currentNote = 'CHigh'
    }
    else if ((index +1) % 9 === 0 || index === 1){
      currentNote = 'B'
    }
    else if ((index +1) % 10 === 0 || index === 2){
      currentNote = 'A'
    }
    else if ((index +1) === 0 || index === 3){
      currentNote = 'G'
    }
    else if ((index +1) === 0 || index === 4){
      currentNote = 'F'
    }
    else if ((index +1) === 0 || index === 5){
      currentNote = 'E'
    }
    else if ((index +1) === 0 || index === 6){
      currentNote = 'D'
    }
    else if (index % 15 === 0 || index === 7){
      currentNote = 'C'
    }

    // else if (index % 18 === 0 || index === 8){
    //   currentNote = 'BLow'
    // }
    // else if (index % 19 === 0 || index === 9){
    //   currentNote = 'ALow'
    // }
    // else if (index % 20 === 0 || index === 10){
    //   currentNote = 'GLow'
    // }
    // else if (index % 21 === 0 || index === 11){
    //   currentNote = 'FLow'
    // }
    // else if (index % 22 === 0 || index === 12){
    //   currentNote = 'ELow'
    // }
    // else if (index % 23 === 0 || index === 13){
    //   currentNote = 'DLow'
    // }
    // else if (index % 24 === 0 || index === 14){
    //   currentNote = 'CLow'
    // }



    return currentNote
  }



  const hearNote = () => {
    new Audio(`./sounds/${instrument}/${indentifyNote()}${instrument}.mp3`).play()
    console.log(`./sounds/${instrument}/${indentifyNote()}${instrument}.mp3`)
  }


  return (
    <>  
        <img id={index} clasName='bg-slate-500' src={note.img} onClick={() => hearNote()}/>  
    </>
  )
}

export default Note
