import { useState, useGlobal } from 'reactn'
import { Navigate, Link } from 'react-router-dom'
import axios from 'axios'


const SignupForm = () => {

    const [token, setToken] = useGlobal("token")
    const [user, setUser] = useGlobal("user")
    const [error, setError ] = useState("")
    const [signedUp, setSignedUp ] = useState(false)
    const [formState, setFormState] = useState({
        username: "",
        email: "",
        password: "",
        confirmPassword: "" 
    })

    const handleChange = (e) => {
        setFormState({
            ...formState,
            [e.target.name] : e.target.value,
        })
    }

    const handleSubmit = async (e) => {
        e.preventDefault() 
        try{
            await axios.post("http://localhost:1337/auth/signup", formState)
            const {data} = await axios.post("http://localhost:1337/auth/login", {
                username: formState.username,
                password: formState.password
            })
            setUser(data.user)
            setToken(data.token)
            setSignedUp(true)
        }
        catch (err){
            setError("Invalid form data")
        }
    }

    return (
        <>
            {error && <div>{error}</div>}
            {signedUp && <Navigate  replace to="/"/>}
            <form onSubmit={handleSubmit} className="flex">
                <h1 className='ml-1 pl-2 pr-2 pt-1 h-[2.5rem] text-2xl text-center'>Signup -</h1>
                <input type="text" name="username" placeholder="Username" onChange={handleChange} value={formState.username} 
                    className="pl-2 h-[2.5rem] w-[10rem] border-2 border-black rounded-l-lg"/>
                <input type="email" name="email" placeholder="Email" onChange={handleChange} value={formState.email} 
                    className="pl-2 h-[2.5rem] w-[10rem] border-2 border-black "/>
                <input type="password" name="password" placeholder="Password" onChange={handleChange} value={formState.password}
                    className="pl-2 h-[2.5rem] w-[10rem] border-2 border-black "/>
                <input type="password" name="confirmPassword" placeholder="Confirm Password" onChange={handleChange} value={formState.confirmPassword} 
                    className="pl-2  h-[2.5rem] border-2 border-black "/>
                <button className='pl-2 pr-2 h-[2.5rem] text-2xl text-center  bg-white border-2 border-black rounded-r-lg'>Submit</button>
              
                <Link className='ml-5 pr-2 pt-1 h-[2.5rem] text-2xl text-center' to="/login">login</Link>
            </form>
        </>
    )
}

export default SignupForm
