import MusicMakerTop from "./MusicMakerTop"
import MusicMakerSheet from "./MusicMakerSheet"
import MusicMakerBottom from "./MusicMakerBottom"

const MusicMakerContainer = ({notes, arraysLists, instrumentLists, playSong }) => {
  const [currnetInstrument, setCurrnetInstrument ] = useGlobal("currnetInstrument")

  const beepNote = () => {
    setCurrnetInstrument('beep')
    new Audio(`./sounds/beep/Cbeep.mp3`).play()
  }

  const meowNote = () => {
    setCurrnetInstrument('meow')
    new Audio(`./sounds/meow/Cmeow.mp3`).play()
  }

  const baseNote = () => {
    setCurrnetInstrument('base')
    new Audio(`./sounds/base/Cbase.mp3`).play()
  }

  const drumNote = () => {
    setCurrnetInstrument('drum')
    new Audio(`./sounds/drum/Cdrum.mp3`).play()
  }

  const pianoNote = () => {
    setCurrnetInstrument('piano')
    new Audio(`./sounds/piano/Cpiano.mp3`).play()
  }

  return (
    <div className="flex flex-col items-center">
      <div className="border-solid border-2 border-sky-500 w-[80rem] h-[5rem] mt-10">
        <div className='flex'>
          <img  src={'./images/beep.gif'} onClick={beepNote} />
          <img  src={'./images/meow.gif'} onClick={meowNote} />
          <img  src={'./images/base.gif'} onClick={baseNote} />
          <img  src={'./images/drum.gif'} onClick={drumNote} />
          <img  src={'./images/piano.gif'} onClick={pianoNote} />
        </div>
      </div>
        <MusicMakerSheet notes={notes} arraysLists={arraysLists} instrumentLists={instrumentLists}/>
        <MusicMakerBottom playSong={playSong}/>
    </div>
  )
}

export default MusicMakerContainer
