import { Navigate } from "react-router-dom";
import { useGlobal } from "reactn"

const LogoutButton = (props) => {
    const [logginLoad, setLogginLoad] = useGlobal("logginLoad")
    const [token, setToken] = useGlobal("token")
    const [user, setUser] = useGlobal("user")
    // const [currentInstrument, setCurrentInstrument ] = useGlobal("currentInstrument")

    const handleClick = () =>{
        setToken(null)
        setUser(null)
        let songList = document.querySelector('#songsList').innerHTML = ''
        // setLogginLoad(!logginLoad)
        // forceStateRerender()
    }

    // const forceStateRerender = () => {
    //     let stateTrick = currentInstrument
    //     setCurrentInstrument('fake')
    //     setCurrentInstrument(stateTrick)
    //   }

    return (
        <>
            {!user && <Navigate replace to={props.to || "/"}/>}
            <button onClick={handleClick}>Logout</button>
        
        </>
    )
}

export default LogoutButton
