const Note = ({ note, index, setCounter } ) => {

  let currentNote = ''

  const indentifyNote = () => {
    if (index % 16 == 0 || index == 0)
    {
      currentNote = 'Cpiano'
    }

    return currentNote
  }



  const hearNote = () => {
    new Audio(`./sounds/Cpiano.mp3`).play()
  }


  return (
    <>  
        <img clasName='bg-slate-500' src={note.img} onClick={() => hearNote()}/>  
    </>
  )
}

export default Note
