import { setGlobal, addCallback, useState, useGlobal } from 'reactn'
import React, { useEffect } from 'react'
import axios from "axios"

import Navbar from "./components/Navbar";
import SongList from './components/SongList';
import MusicMakerContainer from "./components/MusicMakerContainer";


function App() {
  //Fixes audio delay in Safari 
  const AudioContext = window.AudioContext || window.webkitAudioContext;
  const audioCtx = new AudioContext();

  const numberOfColumns = 200
  const [notes, setNotes ] = useGlobal([])  
  const [noteLength, setNoteLength] = useState([])
  const [token, setToken] = useGlobal("token")
  const [newSong, setNewSong] = useGlobal("newSong")
  const [copyArraysLists, setCopyArraysLists] = useGlobal("copyArraysLists")
  const [copyNoteLength, setCopyNoteLength] = useState("copyNoteLength")
  const [copyInstrumentLists, setCopyInstrumentLists] = useState("copyInstrumentLists")
  const [tempo, setTempo] = useGlobal("tempo")
  const [keepGoing, setKeepGoing] = useGlobal("keepGoing")
  const [playInProgress, setPlayInProgress] = useGlobal("playInProgress")



  let notesArray = []
  let instrument = 'piano'


  let halfNoteTempo =  120000 / tempo
  let quarterNoteTempo = 60000 / tempo
  let eighthNoteTempo = 30000 / tempo
  let sixteenthNoteTempo = 15000 / tempo



  useEffect(() => { 
    setNullInArrays()
  })

  let counter = 0

  useEffect(() => {
    setNewSong(false)
    for (let i=1; i < numberOfColumns +1; i++) {

      // setNotes(notes => [...notes, ...notesArray])
      counter += 1
      if (counter === 9) counter = 1

      let note = 'C'
      if (counter === 1) note = 'CHigh'
      else if (counter === 2) note ='B'
      else if (counter === 3) note ='A'
      else if (counter === 4) note ='G'
      else if (counter === 5) note ='F'
      else if (counter === 6) note ='E'
      else if (counter === 7) note ='D'
      else if (counter === 8) note ='C'

      let newNote = {
        name: note,
        img: './images/note.gif',
        instrument: 'piano'
      }
      notesArray.push(newNote)
      setNotes(notesArray)

      let newNoteLength = {
        length: 1000,
        name: '1/4'
      }
      if (counter % 8 === 0) noteLength.push(newNoteLength)


    }
    setCopyArraysLists(arraysLists)
    setCopyNoteLength(noteLength)
    setCopyInstrumentLists(instrument)
    setKeepGoing(true)
    setPlayInProgress(true)

  }, [])


  let playList0 = []
  let playList1 = []
  let playList2 = []
  let playList3 = []
  let playList4 = []
  let playList5 = []
  let playList6 = []
  let playList7 = []
  let soundTypeList0 = []
  let soundTypeList1 = []
  let soundTypeList2 = []
  let soundTypeList3 = []   
  let soundTypeList4 = []   
  let soundTypeList5 = []   
  let soundTypeList6 = []   
  let soundTypeList7 = []  

  let arraysLists =[
    playList0, playList1, playList2, playList3, playList4, playList5, playList6, playList7
  ]

  let instrumentLists = [
    soundTypeList0, soundTypeList1, soundTypeList2, soundTypeList3, soundTypeList4, soundTypeList5, soundTypeList6, soundTypeList7
  ]

  const setNullInArrays = () => {
    for (let i=0; i<numberOfColumns; i++){
      playList0.push(null)
      playList1.push(null)
      playList2.push(null)
      playList3.push(null)
      playList4.push(null)
      playList5.push(null)
      playList6.push(null)
      playList7.push(null)
    }
  }

  let loopCounter = 0


  const updateNoteDuration = () => {
      // get the note length from object and adjust to tempo
      if (copyNoteLength[loopCounter].name === '1/4') {
        return quarterNoteTempo 
      }
      else if (copyNoteLength[loopCounter].name === '1/8') {
        return eighthNoteTempo 
      }
      else if (copyNoteLength[loopCounter].name === '1/16') {
        return sixteenthNoteTempo 
      }
      else if (copyNoteLength[loopCounter].name === '1/2') {
        return halfNoteTempo
      }
  }

  const playSong = () => {

    // handles loading in a song
    if (newSong === true){
      setNewSong(false)
      arraysLists = copyArraysLists
      instrumentLists = copyInstrumentLists
      setNoteLength(copyNoteLength)

      playList0 = arraysLists[0]
      playList1 = arraysLists[1]
      playList2 = arraysLists[2]
      playList3 = arraysLists[3]
      playList4 = arraysLists[4]
      playList5 = arraysLists[5]
      playList6 = arraysLists[6]
      playList7 = arraysLists[7]  
    }
    else{
      setCopyNoteLength(noteLength)
    }

    if(keepGoing) {
      scrollToTarget(loopCounter)

      if (playList0[loopCounter] === undefined){}
      else {
        new Audio(`./sounds/${instrumentLists[0][loopCounter]}/${playList0[loopCounter]}${instrumentLists[0][loopCounter]}.mp3`).play()
        // gsap.to("#note1", {y: "-1rem", ease: "none", duration: .3})
        // gsap.to("#note1", {delay: .3, y: "-0rem", ease: "none", duration: .3})
      }

      if (playList1[loopCounter] === undefined){}
      else {new Audio(`./sounds/${instrumentLists[1][loopCounter]}/${playList1[loopCounter]}${instrumentLists[1][loopCounter]}.mp3`).play()}

      if (playList2[loopCounter] === undefined){}
      else {new Audio(`./sounds/${instrumentLists[2][loopCounter]}/${playList2[loopCounter]}${instrumentLists[2][loopCounter]}.mp3`).play()}

      if (playList3[loopCounter] === undefined){}
      else {new Audio(`./sounds/${instrumentLists[3][loopCounter]}/${playList3[loopCounter]}${instrumentLists[3][loopCounter]}.mp3`).play()}

      if (playList4[loopCounter] === undefined){}
      else {new Audio(`./sounds/${instrumentLists[4][loopCounter]}/${playList4[loopCounter]}${instrumentLists[4][loopCounter]}.mp3`).play()}

      if (playList5[loopCounter] === undefined){}
      else {new Audio(`./sounds/${instrumentLists[5][loopCounter]}/${playList5[loopCounter]}${instrumentLists[5][loopCounter]}.mp3`).play()}

      if (playList6[loopCounter] === undefined){}
      else {new Audio(`./sounds/${instrumentLists[6][loopCounter]}/${playList6[loopCounter]}${instrumentLists[6][loopCounter]}.mp3`).play()}

      if (playList7[loopCounter] === undefined){}
      else {new Audio(`./sounds/${instrumentLists[7][loopCounter]}/${playList7[loopCounter]}${instrumentLists[7][loopCounter]}.mp3`).play()}

      loopCounter += 1
    }
    else{
      loopCounter = 0
    }

    // Function Calls itself to repeat playlist
    setTimeout(playSong, updateNoteDuration());
  }



  let noteSection = document.getElementById('noteSection')

  let distance = -40


  const scrollToTarget = (durationCounter) => {
    distance += 40
    setPlayInProgress(true)
    let startTime = null;
    let requestId;

    const loop = function (currentTime) {
        if (!startTime) {
            startTime = currentTime;
        }

        console.log('are we running?', playInProgress)

        // Elapsed time in miliseconds
        let time = currentTime - startTime;

        let percent = Math.min(time / updateNoteDuration());
        // console.log(noteLength[durationCounter], 'counter', durationCounter)s

        // noteSection.scrollTo(startPos + diff * percent, 0);
        noteSection.scrollTo(distance + 40 * percent, 0);

        if (time < updateNoteDuration() && playInProgress === true) {
            // Continue moving
            requestId = window.requestAnimationFrame(loop);

        } 
        else if (!playInProgress)  {
            window.cancelAnimationFrame(requestId);
            noteSection.scrollTo(0, 0)
            distance = -40
        }
    };
    requestId = window.requestAnimationFrame(loop);
  }


  const logIt = () => {
    setKeepGoing(false)
    setPlayInProgress(false)
    console.log(playInProgress)
  }

  const playAgainTempBTN = () => {
    setKeepGoing(true)
    setPlayInProgress(true)
  }

    return (

      <div className="App">
        <Navbar />

        <button onClick={logIt}>LOG IT</button>
        <SongList notes={notes} setNotes={setNotes} arraysLists={arraysLists} instrumentLists={instrumentLists} noteLength={noteLength} setCopyArraysLists={setCopyArraysLists} setCopyNoteLength={setCopyNoteLength} setCopyInstrumentLists={setCopyInstrumentLists}/>

        {notes && <MusicMakerContainer  notes={notes} arraysLists={arraysLists} instrumentLists={instrumentLists} playSong={playSong} noteLength={noteLength}/> }





      </div>


    );
}

export default App;
