import { useState, useGlobal } from 'reactn'


const Note = ({ note, index, column, playList1, playList2 } ) => {

  // const [playList2, setPlayList2] = useGlobal("playList2")

  let instrument = 'piano'

  const hearNote = () => {
    new Audio(`./sounds/${instrument}/${note.name}${instrument}.mp3`).play()
    // console.log(`./sounds/${instrument}/${indentifyNote()}${instrument}.mp3`)
    addNoteToArray()
  }

  const addNoteToArray = () => {
    playList1[column] = note.name
    if (playList1[column] !== null) playList2[column] = note.name

  }
  
 


  return (
    <>  
        <img id={index} clasName={`column${column} bg-slate-500`} src={note.img} onClick={() => hearNote()}/>  
    </>
  )
}

export default Note
