const Note = ({ note, index, setCounter } ) => {

  let currentNote = ''
  let instrument = 'piano'

  const indentifyNote = () => {
    if ((index + 1) % 8 === 0 || index +1 === 1){
      currentNote = 'CHigh'
    }
    else if ((index + 1) % 9 === 0 || index +1 === 2){
      currentNote = 'B'
    }
    else if ((index + 1) % 10 === 0 || index +1 === 3){
      currentNote = 'A'
    }
    else if ((index +1) === 0 || index +1 === 4){
      currentNote = 'G'
    }
    else if ((index +1) === 0 || index +1 === 5){
      currentNote = 'F'
    }
    else if ((index +1) === 0 || index +1 === 6){
      currentNote = 'E'
    }
    else if ((index +1) === 0 || index +1 === 7){
      currentNote = 'D'
    }
    else if ((index + 1) % 15 === 0 || index === 8){
      currentNote = 'C'
    }


    return note.name
  }



  const hearNote = () => {
    new Audio(`./sounds/${instrument}/${note.name}${instrument}.mp3`).play()
    // console.log(`./sounds/${instrument}/${indentifyNote()}${instrument}.mp3`)
  }


  return (
    <>  
        <img id={index} clasName='bg-slate-500' src={note.img} onClick={() => hearNote()}/>  
    </>
  )
}

export default Note
