import { useState, useGlobal } from 'reactn'
import { Navigate, Link } from 'react-router-dom'
import axios from 'axios'


const LoginForm = () => {
    const [logginLoad, setLogginLoad] = useGlobal("logginLoad")
    const [token, setToken] = useGlobal("token")
    const [user, setUser] = useGlobal("user")
    const [error, setError ] = useState("")
    const [loggedIn, setloggedIn ] = useState(false)
    const [formState, setFormState] = useState({
        username: "",
        password: "",
    })

    const handleChange = (e) => {
        setFormState({
            ...formState,
            [e.target.name] : e.target.value,
        })
    }

    const handleSubmit = async (e) => {
        e.preventDefault()
        try {
            const { data } = await axios.post("http://localhost:1337/auth/login", formState)
            console.log(data.user)
            setUser(data.user)
            setToken(data.token)
            setloggedIn(true)
            setLogginLoad(!logginLoad)
        }
        catch (err){
            console.log(err)
            setError(err)
        }
        
    }

    return(
        <>
            {loggedIn && <Navigate replace to="/" />}
            <form onSubmit={handleSubmit} className="flex mt-6">
                <h1 className='ml-1 pl-2 pr-2 pt-1 h-[2.5rem] text-2xl text-center'>Login -</h1>
                <input className=' pl-2 h-[2.5rem] border-2 border-black rounded-l-lg'
                    type="text" 
                    name="username" 
                    placeholder="Username" 
                    onChange={handleChange} 
                    value={formState.username} />
                <input  className=' pl-2 h-[2.5rem] border-2 border-black'
                    type="password" 
                    name="password" 
                    placeholder="Password" 
                    onChange={handleChange} 
                    value={formState.password}/>
                <button className=' pl-2 pr-2 pt-1 text-2xl text-center  bg-green-500 border-2 border-black rounded-r-lg'>Login</button>
                <Link className='ml-16 pl-2  pt-1 text-2xl text-center  border-black rounded-lg' to="/signup"> Signup</Link>
            </form>
        </>
    )

}


export default LoginForm
