  // const [playList2, setPlayList2] = useGlobal("playList2")

  let instrument = 'piano'

  const hearNote = () => {
    new Audio(`./sounds/${instrument}/${note.name}${instrument}.mp3`).play()
    // console.log(`./sounds/${instrument}/${indentifyNote()}${instrument}.mp3`)
    addNoteToArray()
    // console.log('playList1', playList1)
    // console.log('playList2', playList2[0] !== null)
  }

  const addNoteToArray = () => {
    if (playList1[column] !== null) playList2[column] = note.name
    else if (playList0[column] !== null) playList1[column] = note.name
    else playList0[column] = note.name
    
    
    
    console.log('playList1', playList0)
    console.log('playList2', playList2)
    // console.log('playList3', playList3)

    console.log("PlayListArray", arraysLists[0])
  }
  
 


  return (
    <>  
        <img id={index} clasName={`column${column} bg-slate-500`} src={note.img} onClick={() => hearNote()}/>  
    </>
  )
}

export default Note
