const Note = ({ note, index, setCounter } ) => {

  let currentNote = ''
  let instrument = 'piano'

  const indentifyNote = () => {
    if (index % 16 === 0 || index === 0){
      currentNote = 'CHigh'
    }
    else if (index % 11 === 0 || index === 1){
      currentNote = 'B'
    }
    else if (index % 12 === 0 || index === 2){
      currentNote = 'A'
    }

    return currentNote
  }



  const hearNote = () => {
    new Audio(`./sounds/${indentifyNote()}${instrument}.mp3`).play()
  }


  return (
    <>  
        <img clasName='bg-slate-500' src={note.img} onClick={() => hearNote()}/>  
    </>
  )
}

export default Note
