import { useState, useGlobal } from 'reactn'


const Note = ({ note, index, column, playList1, playList2, playList3, arraysLists } ) => {

  // const [playList2, setPlayList2] = useGlobal("playList2")

  let instrument = 'piano'

  const hearNote = () => {
    new Audio(`./sounds/${instrument}/${note.name}${instrument}.mp3`).play()
    // console.log(`./sounds/${instrument}/${indentifyNote()}${instrument}.mp3`)
    addNoteToArray()
    // console.log('playList1', playList1)
    // console.log('playList2', playList2[0] !== null)
  }

  const addNoteToArray = () => {
    if (arraysLists[1].playList[column] !== null) arraysLists[2].playList[column] = note.name
    else if (arraysLists[0].playList[column] !== null) arraysLists[1].playList[column] = note.name
    else arraysLists[0].playList[column] = note.name
    
    
    
    // console.log('playList1', arraysLists.playList1)
    // console.log('playList2', arraysLists.playList2)
    // console.log('playList3', arraysLists.playList3)

    console.log("AL", arraysLists[0].playList[column])
  }
  
 


  return (
    <>  
        <img id={index} clasName={`column${column} bg-slate-500`} src={note.img} onClick={() => hearNote()}/>  
    </>
  )
}

export default Note
