import { setGlobal, useState, useGlobal, useEffect } from 'reactn'
import NameForm from './NameForm'
import axios from "axios"

const SongList = ({notes, setNotes, arraysLists, noteLength, instrumentLists, setCopyArraysLists, setCopyNoteLength, setCopyInstrumentLists }) => {
    const [token, setToken] = useGlobal("token")
    const [newSong, setNewSong] = useGlobal("newSong")
    const [songName, setSongName] = useGlobal("songName")
    const [songs, setSongs] = useState('')
    const [currentInstrument, setCurrentInstrument ] = useGlobal("currentInstrument")


    useEffect(() => {
      getSongs()
    }, [])

    const getSongs = async () =>{
      console.log('made it?')
      const res = await axios.get("http://localhost:1337/song/")
      setSongs(res.data)

    }


    const saveSong = async () => {

        console.log("notes?", notes)
        // console.log("ARRAYS")
        // console.log(token)
        // console.log(arraysLists)
        // console.log("LENGTH", noteLength)
        // console.log("IL", instrumentLists)
        try{
          await axios.post("http://localhost:1337/song/", {
            songName: songName,
            user: "629845e4c1fe79a6d09a4117",
            noteArray: arraysLists,
            noteLength: noteLength,
            instruments: instrumentLists,
            notes: notes,
          },
          {
            headers: {
              "Authorization": `Bearer ${token}`
            }
          })
        }
        catch (err){
          return "Invalid song info"
        }
        getSongs()
      }

      const loadSong = () => {
        console.log("^^^^^^^^", songs)

        setNewSong(true)
        try{
            axios.get(`http://localhost:1337/song/${songName}/`)
            .then(res => {
                setCopyArraysLists(res.data.noteArray)
                setCopyNoteLength(res.data.noteLength)
                setCopyInstrumentLists(res.data.instruments)
                setNotes(res.data.notes)
            })

        }
        catch (err){
          return "Invalid song info"
        }
      }

      const selectSong = (song) => {
        console.log(song)
        setSongName(song)
      }

      const deleteSong = async (song) => {
        try {
         await axios.delete(`http://localhost:1337/song/${song}` , {
            headers: {
              "Authorization": `Bearer ${token}`
            }
          })
        }
        catch (err){
          console.error(err)
        }
        
        getSongs()
      }


      const logIt = () => {
        console.log("Notes", notes)
      }




  return (
    <>
        <button onClick={saveSong}>Save Song</button>
        <button onClick={loadSong}>load Song</button>
        {/* <button onClick={logIt}>LOG IT</button> */}
        < NameForm />
        {songs && songs.map(song => 
          <div className='flex'>
            <p onClick={() => selectSong(song.songName)}> {song.songName} -</p>
            <button onClick={() => loadSong(song.songName)}>delete</button>
             <button onClick={() => deleteSong(song.songName)}>delete</button>
          </div>
        )}



        
    </>
  )
}

export default SongList
